INSERT INTO `mantencion_base` VALUES (13,'Revisión Sistema Eléctrico','ALL','ALL','REVISION','Esta revisión es clave para mantener en buenas condiciones el sistema de carga de la batería y el encendido a través del motor de arranque. Igualmente mantener en beunas condiicones el sitema de luces evita sobrecargas eléctricas y contribuye en la conducción nocturna','Revisión de: Luces/Fusibles, Carga del Alternador y Motor de Arranque',NULL,1,30000,NULL,'2014-11-12 00:07:32');
